// DOM Elements
const elements = {
    form: document.getElementById('questionsForm'),
    startButton: document.querySelector('.startNow'),
    containerChart: document.querySelector('.containerChart'),
    footer: document.querySelector('.footer'),
    footerFormSection: document.querySelector('.footerFormSection'),
    footerFormSection2: document.querySelector('.footerFormSection2'),
    section1: document.getElementById('section1'),
    redPractice: document.getElementById('redPractice'),
    yellowPractice: document.getElementById('yellowPractice'),
    greenPractice: document.getElementById('greenPractice')
};

// Chart Configuration
const chartConfig = {
    type: 'doughnut',
    options: {
        responsive: true,
        cutout: '80%',
        plugins: {
            tooltip: { enabled: false }
        }
    },
    plugins: [{
        id: 'centerText',
        beforeDraw(chart) {
            const { width, height, ctx } = chart;
            ctx.restore();
            const fontSize = (height / 5).toFixed(2);
            ctx.font = `${fontSize}px Arial`;
            ctx.textBaseline = 'middle';
            ctx.fillStyle = '#000000';
            const text = chart.data.datasets[0].data[0].toString();
            const textX = Math.round((width - ctx.measureText(text).width) / 2);
            const textY = height / 2;
            ctx.fillText(text, textX, textY);
            ctx.save();
        }
    }]
};

// Question Management
const QuestionManager = {
    generateQuestions() {
        questions.forEach((question, index) => {
            const questionRow = this.createQuestionRow(question, index);
            elements.form.appendChild(questionRow);
            this.addButtonListeners(questionRow, index);
        });
    },

    createQuestionRow(question, index) {
        const questionRow = document.createElement('div');
        questionRow.className = `question-row ${index === 0 ? 'active' : ''}`;
        questionRow.innerHTML = `
            <div class="qQuestion">
                <div class="questionNumber">${question.number}</div>
                <div class="question">${question.text}</div>
                <div class="buttonContainer">
                    <input class="aButton" type="button" name="q${index + 1}" value="Yes">
                    <input class="aButton" type="button" name="q${index + 1}" value="No">
                    ${question.hasNotApplicable ? `
                    <input class="aButton" type="button" name="q${index + 1}" value="Not Applicable">
                    ` : ''}
                </div>
            </div>
            <div class="iconContainer" style="background-color: ${question.color}">
                ${question.svg}
            </div>
        `;
        return questionRow;
    },

    addButtonListeners(questionRow, index) {
        const buttons = questionRow.querySelectorAll('input[type="button"]');
        buttons.forEach(button => {
            button.addEventListener('click', () => {
                buttons.forEach(btn => btn.classList.remove('selected'));
                button.classList.add('selected');
                StateManager.updateYesCount();
                NavigationManager.showNextQuestion();
            });
        });
    }
};

// Navigation Management
const NavigationManager = {
    showNextQuestion() {
        const currentRow = document.querySelector(`.question-row:nth-of-type(${state.currentQuestion})`);
        if (!currentRow) return;

        currentRow.classList.add('fade-out');
        
        setTimeout(() => {
            currentRow.classList.remove('active');
            state.currentQuestion++;
            
            if (state.currentQuestion <= questions.length) {
                this.handleNextQuestion();
            } else {
                this.handleLastQuestion();
            }
        }, 0);
    },

    handleNextQuestion() {
        const nextRow = document.querySelector(`.question-row:nth-of-type(${state.currentQuestion})`);
        if (nextRow) {
            nextRow.classList.add('active');
            nextRow.classList.remove('fade-out');
            setTimeout(() => {
                const nextQuestionPosition = nextRow.getBoundingClientRect().top + window.scrollY - CONFIG.SCROLL_OFFSET;
                window.scrollTo({
                    top: nextQuestionPosition,
                    behavior: 'smooth'
                });
            }, CONFIG.ANIMATION_DELAY);
        }
    },

    handleLastQuestion() {
        if (elements.footer && elements.footerFormSection) {
            elements.footer.classList.add('active');
            elements.footerFormSection.classList.add('active');
            const footerPosition = elements.footer.getBoundingClientRect().top + window.scrollY - CONFIG.SCROLL_OFFSET;
            window.scrollTo({
                top: footerPosition,
                behavior: 'smooth'
            });
        }
    }
};

// State Management
const StateManager = {
    updateYesCount() {
        state.yesCount = 0;
        state.answerCount = 0;

        for (let i = 1; i <= questions.length; i++) {
            const selectedButton = document.querySelector(`input[name="q${i}"].selected`);
            if (selectedButton) {
                state.answerCount++;
                if (selectedButton.value === "Yes") {
                    state.yesCount++;
                }
            }
        }

        this.updateUI();
        ChartManager.updateChart(state.yesCount);
    },

    updateUI() {
        if (state.answerCount === questions.length && state.formSubmitted) {
            window.scrollTo({ top: 0, behavior: 'smooth' });
            elements.containerChart.style.display = 'flex';
            elements.footer.style.backgroundColor = CONFIG.CHART_COLORS.GRAY;
            elements.footer.style.display = 'none';
            elements.footerFormSection.classList.remove('active');
            elements.footerFormSection2.classList.add('active');
            elements.section1.style.display = 'none';
        } else {
            elements.containerChart.style.display = 'none';
            elements.footer.style.backgroundColor = '#1E2032';
            elements.footerFormSection.classList.add('active');
            elements.footerFormSection2.classList.remove('active');
            elements.section1.style.display = 'block';
        }
    }
};

// Chart Management
const ChartManager = {
    updateChart(yesCount) {
        this.updateChartColor(yesCount);
        this.updateChartDisplay(yesCount);
    },

    updateChartColor(yesCount) {
        if (yesCount > 14) {
            state.chartColor = CONFIG.CHART_COLORS.GREEN;
        } else if (yesCount > 9) {
            state.chartColor = CONFIG.CHART_COLORS.YELLOW;
        } else if (yesCount > 0) {
            state.chartColor = CONFIG.CHART_COLORS.RED;
        }
    },

    updateChartDisplay(yesCount) {
        elements.redPractice.style.display = yesCount > 0 && yesCount <= 9 ? 'block' : 'none';
        elements.yellowPractice.style.display = yesCount > 9 && yesCount <= 14 ? 'block' : 'none';
        elements.greenPractice.style.display = yesCount > 14 ? 'block' : 'none';

        const chartData = {
            datasets: [{
                label: 'Number of responses',
                data: [yesCount, 20 - yesCount],
                backgroundColor: [state.chartColor, CONFIG.CHART_COLORS.GRAY],
                borderRadius: [0, 0],
                borderWidth: 0,
                spacing: -0
            }]
        };

        if (window.myChart) {
            window.myChart.data = chartData;
            window.myChart.update();
        } else {
            const ctx = document.getElementById('scoreChart').getContext('2d');
            window.myChart = new Chart(ctx, {
                ...chartConfig,
                data: chartData
            });
        }
    }
};

// Event Handlers
const EventHandlers = {
    handleStartButtonClick() {
        const y = elements.form.getBoundingClientRect().top + window.scrollY - CONFIG.SCROLL_OFFSET;
        window.scrollTo({ top: y, behavior: 'smooth' });
    },

    setFormSubmittedTrue() {
        state.formSubmitted = true;
        StateManager.updateYesCount();
    }
};

// Initialize
document.addEventListener('DOMContentLoaded', () => {
    QuestionManager.generateQuestions();
    elements.startButton.addEventListener('click', EventHandlers.handleStartButtonClick);
});

// Export functions that need to be accessed globally
window.setFormSubmittedTrue = EventHandlers.setFormSubmittedTrue;

